//使用node-gyp rebuild --target=1.4.13 --arch=ia32 --dist-url=https://atom.io/download/atom-shell产生工程
//--target=1.4.3，使用electron的版本
//--arch=ia32或者x64
//编译出的代码被做成模块(由js代码exports出来,package.json描述模块)
//放在node_modules中,被electron使用

const os = require('os');
var platform = os.platform();

var addon = null;
//console.log(platform);
if(platform=="win32")
	addon = require("./build/Release/hello");
else if(platform=="darwin")
	addon = require("./bin/Release/hello");

exports.f1 = addon.f1;
